import React, { useState } from 'react';
import { Content, Header } from 'antd/lib/layout/layout';
import { Layout, Row, Col, DatePicker, TimePicker, Select, Card, Menu, Image } from 'antd';
import moment from 'moment'
import 'antd/dist/antd.css';
import '../App.css';

const items = [
    {
      label: 'Home Page',
      key: '/',
    },
]

var checkDate = false
var checkTime = false

var optionList = [{
  }]

function MainPage(props) {
    const [date, setDate] = useState("");
    const [time, setTime] = useState("");
    const [areaMetadata, setMetadata] = useState([]);
    const [forecasts, setForecasts] = useState([]);
    const [images, setImages] = useState([]);
    const [disableLocation, setDisabled] = useState(true);
    const [selectedValue, setSelected] = useState("");
    const [imageLink, setImageLink] = useState("data:image/png;base64,");
    const onClick = (e) => {
        console.log('click ', e);
    };

    const onChangeDate = (date, dateString) => {
        console.log(date, dateString);
        setDate(dateString);
        checkDate = true
        pullData()
    };

    const onChangeTime = (time, timeString) => {
        console.log(time, timeString);
        setTime(timeString);
        checkTime = true
        pullData()
    };
    
    const handleChange = (value) => {
        console.log(`selected ${value}`);
        setSelected(value)
        renderImage(value)
    };

    var momentObj = moment(date + time, 'YYYY-MM-DDLT');
    var combined = momentObj.format('YYYY-MM-DD[T]HH:mm:ss');
    var weatherURL = "https://api.data.gov.sg/v1/environment/2-hour-weather-forecast?date_time=" + encodeURIComponent(combined);
    var imageURL = "https://api.data.gov.sg/v1/transport/traffic-images?date_time=" + encodeURIComponent(combined);
    
    const pullData = () => {
        console.log(checkDate, checkTime)
        var forecasts = []
        if (checkDate == true && checkTime == true){
            fetch(weatherURL)
            .then(res => res.json())
            .then(
                (result) => {
                    if (result.message == 'invalid datetime format') {
                        setForecasts([])
                        forecasts = []
                        setMetadata([])
                    } else {
                        // console.log(result.area_metadata)
                        setForecasts(result.items[0].forecasts)
                        forecasts = result.items[0].forecasts
                        setMetadata(result.area_metadata)
                    }

                    var temp = []
                    if (forecasts.length == 0) {
                        optionList = forecasts
                        setDisabled(true)
                    }
                    else {
                        setDisabled(false)
                        for (let fore in forecasts) {
                            temp.push({value: forecasts[fore].area})
                        }
                        optionList = temp
                    }
                },

                (error) => {
                console.log("Error Getting Results")
                }
            )

            fetch(imageURL)
            .then(res => res.json())
            .then(
                (result) => {
                    if (result.message == 'invalid datetime format') {
                        setImages([])
                    } else {
                        setImages(result.items[0].cameras)
                    }
                },

                (error) => {
                console.log("Error Getting Results")
                }
            )
            checkDate = false
            checkTime = false
        }
    }

    const renderForecast = () => {
        var area = "None"
        var forecast = "None"
        if (selectedValue != "") {
            for (let cast in forecasts) {
                if (forecasts[cast].area == selectedValue) {
                    area = selectedValue
                    forecast = forecasts[cast].forecast
                }
            }
        }
        return(
            <div>
                <p>Area: {area}</p>
                <p>Forecast: {forecast}</p>
            </div>
        )
    }

    const renderImage = (selectedValue) => {
        console.log(images)
        // var src = "data:image/png;base64,"
        var closestDistance = 0
        var smallestImage = 0
        var latitude = 0
        var longitude = 0
        if (selectedValue != "") {
            for (let data in areaMetadata) {
                if (areaMetadata[data].name == selectedValue) {
                    latitude = areaMetadata[data].label_location.latitude
                    longitude = areaMetadata[data].label_location.longitude
                    break
                }
            }
            for (let image in images) {
                var iLat = images[image].location.latitude
                var iLong = images[image].location.longitude
                var distance = Math.sqrt( Math.pow((latitude-iLat), 2) + Math.pow((longitude-iLong), 2) );
                console.log(distance)
                if (image == 0) {
                    closestDistance = distance
                }
                else if (distance < closestDistance) {
                    closestDistance = distance
                    smallestImage = image
                }
            }
            console.log(images[smallestImage].image)
            console.log(closestDistance)
            setImageLink(images[smallestImage].image)
        }
    }


    return (
        <div>
            <Layout style={{height:"100vh", overflow: "auto"}}>
                <Header>
                    <div className="logo" />
                    <Menu
                        theme="dark"
                        mode="horizontal"
                        defaultSelectedKeys={['/']}
                        items={items}
                        onClick={onClick}
                    />
                </Header>
                    <Content className='content' style={{ padding: '50px 50px' }}>
                        <div className="site-layout-content">
                            <Row align="middle">
                                <Col span={12} align="middle">
                                    <DatePicker onChange={onChangeDate} style={{ color: "#ffffff", width: "30vw", textAlign: 'center', margin: "10px", height: '5vh' }} />
                                </Col>
                                <Col span={12} align="middle">
                                    <TimePicker minuteStep={15} format="HH:mm" onChange={onChangeTime} style={{ color: "#ffffff", width: "30vw", textAlign: 'center', margin: "10px", height: '5vh' }} />
                                </Col>
                                
                            </Row>
                            <Row align="middle">
                                <Col span={12} align="middle">
                                    <Select
                                        disabled={disableLocation}
                                        placeholder="Select a Location"
                                        style={{
                                            color: "#000000", width: "30vw", textAlign: 'start', margin: "10px", height: '5vh'
                                        }}
                                        onChange={handleChange}
                                        options={optionList}
                                        
                                    />
                                </Col>        
                                <Col span={12 } align="middle">
                                    <Card title="Weather" style={{ width: 300, textAlign: "start" }}>
                                        {/* <p>Area: {selectedValue}</p>
                                        <p>Forecast: {forecasts[selectedValue].forecast}</p> */}
                                        {renderForecast()}
                                    </Card>
                                </Col>
                            </Row>
                            <Row align="middle">
                                <Col span={24} align="middle">
                                    {/* {renderImage()} */}
                                    <Image
                                        // width={200}
                                        // height={200}
                                        src={imageLink}
                                        fallback="data:image/png;base64,"
                                    />
                                </Col>        
                            </Row>
                        </div>
                    </Content>
                </Layout>
        </div >
    );
}

export default MainPage;